OpenClaims_1.query:
q = load "${App.Datasets.Claims_tp.FullyQualifiedName}";q = filter q by 'Policy_PolicyOwner_Name' == "{{cell(Selected_Customer_1.result, 0, "PolicyOwner_Name").asObject()}}";
q = filter q by ('Status' in ["Active","Coverage Confirmed","Coverage Evaluation","In Dispute","Initial","Notified","Open","Pending""Reopened","Under Evaluation","Under Negotiation"]);
q = foreach q generate 'Name' as 'Claim Name',('EstimatedAmount') as 'Estimated Amount',daysBetween(toDate(CreatedDate_sec_epoch), now() ) as 'Days Open';q = group q by 'Claim Name';
q = foreach q generate 'Claim Name' as 'Claim Name', sum('Estimated Amount') as 'Estimated Amount',sum('Days Open') as 'Days Open', case when sum('Days Open') >= {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}}  then "1. Not Behind" when sum('Days Open') - {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}} >5 then  "2. 0-5 Days Behind" when sum('Days Open') - {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}} >5 and sum('Days Open') - {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}} < 15 then "3. 5-15 Days Behind" else "4. Behind" end as 'Days vs Average',case when sum('Days Open') >= {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}} then "rgb(0, 181, 49)" when sum('Days Open') - {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}} >5 then  "rgb(255, 255, 102)" when sum('Days Open') - {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}} >5 and sum('Days Open') - {{cell(DaysToOpen_Avg_1.result, 0, "Days Open Avg").asString()}} < 15 then "rgb(255, 173, 51)" else "rgb(181, 14, 3)" end as 'Activity Status Color';q = foreach q generate 'Claim Name','Estimated Amount', 'Days Open', 'Days vs Average','Activity Status Color';
q= limit q 20;

TotalCustomers_1.query:
q = load "${App.Datasets.Insurance_Policies_tp.FullyQualifiedName}";
q = group q by all;
q = foreach q generate unique('PolicyOwner_Name') as 'unique_PolicyOwner_Name';
q = limit q 2000;

Selected_Customer_1.query:
q = load "${App.Datasets.Insurance_Policies_tp.FullyQualifiedName}";
q = group q by 'PolicyOwner_Name';
q = foreach q generate first('PolicyOwner_Name') as 'PolicyOwner_Name',coalesce(sum('Claims_ActualAmount'),0) as 'Claims_ActualAmount', coalesce(sum('Claims_ApprovedAmount'),0) as 'Claims_ApprovedAmount', coalesce(sum('Claims_Count'),0) as 'Claims_Count', coalesce(sum('Claims_EstimatedAmount'),0) as 'Claims_EstimatedAmount', coalesce(sum('GrossWrittenPremium'),0) as 'GrossWrittenPremium', first('Id') as 'Id', first('NameInsuredId') as 'NameInsuredId',round(coalesce(sum('Claims_ApprovedAmount'),0)/coalesce(sum('GrossWrittenPremium'),0),2) as 'Loss Ratio';
q = limit q 100;

CustomerMetrics_1.query:
q = load "${App.Datasets.Insurance_Policies_tp.FullyQualifiedName}";
q = group q by all ;
q = foreach q generate coalesce(avg('GrossWrittenPremium'),0) as 'GrossWrittenPremium_forAll',  coalesce(avg('Claims_ApprovedAmount'),0) as 'Claims_ApprovedAmount',
coalesce(avg('Claims_Count'),0) as 'Claims_Count',coalesce(round(avg('Claims_ApprovedAmount')/avg('GrossWrittenPremium'),2),0) as 'Loss Ratio';
q = limit q 100;

CompareClaims_1.query:
q = load "${App.Datasets.Insurance_Policies_tp.FullyQualifiedName}";
q = group q by all;
q = foreach q generate case when {{cell(Selected_Customer_1.result, 0, "Claims_Count").asObject()}} >= {{cell(CustomerMetrics_1.result, 0, "Claims_Count").asObject()}} then "higher" else "lower" end as 'compare_claimcount',case when {{cell(Selected_Customer_1.result, 0, "Claims_ApprovedAmount").asObject()}} >= {{cell(CustomerMetrics_1.result, 0, "Claims_ApprovedAmount").asObject()}} then "higher" else "lower" end as 'compare_claimamount', count() as 'count';
q = limit q 2000;

ThisCustomerRank_1.query:
q = load "${App.Datasets.Insurance_Policies_tp.FullyQualifiedName}";q = filter q by 'PolicyOwner_Name' == "{{cell(Selected_Customer_1.result, 0, "PolicyOwner_Name").asObject()}}";
result = group q by 'PolicyOwner_Name';
result = foreach result generate q.'PolicyOwner_Name' as 'PolicyOwner_Name', sum(q.'GrossWrittenPremium') as 'GrossWrittenPremium';
result = group result by 'PolicyOwner_Name';
result = foreach result generate 'PolicyOwner_Name', sum('GrossWrittenPremium') as 'GrossWrittenPremium', row_number() over([..] partition by all order by sum(GrossWrittenPremium) desc) as 'Rank';
result = order result by ('GrossWrittenPremium' desc);
result = limit result 2000;

DaysToOpen_Avg_1.query:
q = load "${App.Datasets.Claims_tp.FullyQualifiedName}";
q = filter q by !('Status' in ["Approved", "Rejected"]);
q = foreach q generate 'Name' as 'Name','Status' as 'Status', 'CreatedDate_Year' + "~~~" + 'CreatedDate_Month' + "~~~" + 'CreatedDate_Day' as 'CreatedDate_Year~~~CreatedDate_Month~~~CreatedDate_Day', sum('EstimatedAmount') as 'sum_EstimatedAmount',daysBetween(toDate(CreatedDate_sec_epoch), now() ) as 'Days Open';
q = group q by all;
q = foreach q generate avg('Days Open') as 'Days Open Avg';

ActiveClaims_1.query:
q = load "${App.Datasets.Insurance_Policies_tp.FullyQualifiedName}";
q = filter q by 'PolicyOwner_Name' == "{{cell(Selected_Customer_1.result, 0, "PolicyOwner_Name").asObject()}}";
q = group q by 'PolicyType';
q = foreach q generate 'PolicyType' as 'PolicyType', unique('Id') as '# Policies', sum('GrossWrittenPremium') as 'GWP', sum('Claims_Count') as '# Claims',round(sum('Claims_ApprovedAmount')/sum('GrossWrittenPremium'),2) as 'Loss Ratio';
q = order q by 'PolicyType' asc;
q = limit q 2000;
