lens_3.query:
q = load "${App.Datasets.FullModelInputWithOutletKPI_tp.FullyQualifiedName}";
q = group q by 'BrandFamily';
q = foreach q generate 'BrandFamily' as 'BrandFamily', avg('prediction_PredictedQuantity') as 'avg_prediction_PredictedQuantity';
q = order q by 'avg_prediction_PredictedQuantity' desc;
q = limit q 5;

lens_2.query:
q = load "${App.Datasets.FullModelInputWithOutletKPI_tp.FullyQualifiedName}";
q = foreach q generate 'BRAND' as 'BRAND', 'prediction_PredictedQuantity' as 'prediction_PredictedQuantity';
q = order q by 'prediction_PredictedQuantity' desc;
q = limit q 5;
