Sales_Rep_Performanc_1.query:
q = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
q = filter q by date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day') in ["current quarter".."current day"];
result = group q by 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_Visitor_UniqueUserName';
result = foreach result generate q.'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_Visitor_UniqueUserName' as 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_Visitor_UniqueUserName', dense_rank() over([..] partition by all order by sum('TotalPrice') desc) as 'Owner_Rank', sum('TotalPrice') as 'TotalPrice', avg(sum('TotalPrice')) over ([..] partition by all) as 'avg_TotalPrice_by_all';
result_final = foreach result generate 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_Visitor_UniqueUserName' as 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_Visitor_UniqueUserName', sum('TotalPrice') as 'TotalPrice', round(sum('avg_TotalPrice_by_all'),2) as 'avg_TotalPrice_by_all', case when sum('TotalPrice') >= sum('avg_TotalPrice_by_all') then "Above Average" else "Below Average" end as 'Above_Below_Average';
result = order result_final by 'TotalPrice' asc;
result = limit result 5;

Stores_wo_Top_Products_This_Quarter_KPI_.query:
orderitem = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
orderitem = filter orderitem by date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day') in ["current quarter".."current day"];
orderitem = group orderitem by ('Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Id', 'Product_Name', 'Product2Id');
orderitem = foreach orderitem generate 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Id' as 'RetailStoreId', 'Product_Name' as 'Product_Name', 'Product2Id' as 'Product.Id', sum('Quantity') as 'Quantity';

retailstore = load "${App.Datasets.CG_RetailStore_with_All_Products5_tp.FullyQualifiedName}";
retailstore = group retailstore by ('Id', 'Product_Id_with_Name');
retailstore = foreach retailstore generate 'Id' as 'RetailStoreId', 'Product_Id_with_Name' as 'Product_Id_with_Name', substr('Product_Id_with_Name',index_of('Product_Id_with_Name',"_")+1) as 'Product_Name', substr('Product_Id_with_Name',1,index_of('Product_Id_with_Name',"_")-1) as 'Product.Id',  -1 as 'dummy';

result = cogroup orderitem by ('RetailStoreId','Product_Name', 'Product.Id') full, retailstore by ('RetailStoreId','Product_Name', 'Product.Id');
result = foreach result generate coalesce(retailstore.'RetailStoreId',orderitem.'RetailStoreId') as 'RetailStoreId', coalesce(retailstore.'Product_Name',orderitem.'Product_Name') as 'Product_Name', coalesce(retailstore.'Product.Id', orderitem.'Product.Id') as 'Product.Id', coalesce(sum(orderitem.'Quantity'),0) as 'Quantity';

result = filter result by 'Quantity' == 0;
result = filter result by 'Product.Id' in {{coalesce(column(Top_5_Products_Quantity_1.result, ["Product2Id"]),"[]").asString()}};
result = filter result by 'RetailStoreId' in {{coalesce(column(All_Stores_This_Quarter_1.result, ["RetailStoreId"]),"[]").asString()}};
result = group result by all;
result = foreach result generate unique('RetailStoreId') as '# Stores';

QOQ_Change_in_Sales__2.query:
q = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
q_A = filter q by date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day') in 
["1 quarter ago".."current day - 1 quarter"];
q_B = filter q by date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day') in ["current quarter".."current day"];
result = group q_A by 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name' full, q_B by 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name';
result = foreach result generate coalesce(q_A.'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name', q_B.'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name') as 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name', sum(q_A.'Quantity') as 'Previous_Qtr_Quantity', 
coalesce(sum(q_B.'Quantity'),0) as 'Current_Qtr_Quantity';
result = foreach result generate 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name', 'Previous_Qtr_Quantity', 'Current_Qtr_Quantity', (Current_Qtr_Quantity - Previous_Qtr_Quantity) / Previous_Qtr_Quantity as 
'Percent_Change_Quantity';
result = foreach result generate case when 'Percent_Change_Quantity' < 0 then 1 else 0 end as 'Count_of_Stores_Declined';
result = group result by all;
result = foreach result generate sum('Count_of_Stores_Declined') as 'Count_of_Stores_Declined';

