Clone_of_Clone_of_Ti_1.query:
-- Incomplete High Priority Visits
visit1 = load "${App.Datasets.CG_Visit5_tp.FullyQualifiedName}";
visit1 = filter visit1 by 'Status' in ["Abandoned", "Planned"] and  'VisitPriority' == "High" and  date('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Month', 'PlannedVisitStartTime_Day') in [.."1 day ago"];
visit1 = group visit1 by ('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Week');
visit1 = foreach visit1 generate 'PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Week', count(visit1) as 'High_Priority_Incomplete_Visits';
visit1 = fill visit1 by (dateCols=('PlannedVisitStartTime_Year','PlannedVisitStartTime_Week', "Y-W"));
visit1 = timeseries visit1 generate 'High_Priority_Incomplete_Visits' as 'Incomplete_Visits' with (length=4, predictionInterval=80, 
    dateCols=('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Week', "Y-W"), seasonality=12);
visit1 = foreach visit1 generate 'PlannedVisitStartTime_Year' + "~~~" + 'PlannedVisitStartTime_Week' as 'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week', coalesce('High_Priority_Incomplete_Visits', 'Incomplete_Visits') as 'Incomplete_Visits', 'Incomplete_Visits_low_80', 'Incomplete_Visits_high_80';
visit1 = order visit1 by 'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week' desc;
-- Sales Details
sale1 = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
sale1 = group sale1 by ('Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');
sale1 = foreach sale1 generate 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week', sum('TotalPrice') as 'sum_TotalPrice';
sale1 = fill sale1 by (dateCols=('Order_ActivatedDate_Year','Order_ActivatedDate_Week', "Y-W"));
sale1 = timeseries sale1 generate 'sum_TotalPrice' as 'Sales' with (length=4, predictionInterval=80, 
    dateCols=('Order_ActivatedDate_Year', 'Order_ActivatedDate_Week', "Y-W"), seasonality=12);
sale1 = foreach sale1 generate 'Order_ActivatedDate_Year' + "~~~" + 'Order_ActivatedDate_Week' as 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week', coalesce('sum_TotalPrice', 'Sales') as 'Sales', 'Sales_low_80', 'Sales_high_80';
sale1 = order sale1 by 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week' desc;
--Quantity
qty1 = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
qty1 = group qty1 by ('Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');
qty1 = foreach qty1 generate 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week', sum('TotalPrice') as 'sum_Quantity';
qty1 = fill qty1 by (dateCols=('Order_ActivatedDate_Year','Order_ActivatedDate_Week', "Y-W"));
qty1 = timeseries qty1 generate 'sum_Quantity' as 'Quantity' with (length=4, predictionInterval=80, 
    dateCols=('Order_ActivatedDate_Year', 'Order_ActivatedDate_Week', "Y-W"), seasonality=12);
qty1 = foreach qty1 generate 'Order_ActivatedDate_Year' + "~~~" + 'Order_ActivatedDate_Week' as 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week', coalesce('sum_Quantity', 'Quantity') as 'Quantity', 'Quantity_low_80', 'Quantity_high_80';
qty1 = order qty1 by 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week' desc;
--Sales Velocity

sv1  = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
sv1 = group sv1 by ('Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');
sv1 = foreach sv1 generate 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week', sum('TotalPrice') as 'Sales',unique('Order_ActivatedDate_Week') as 'unique_week';
sv1 = group sv1 by ('Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');sv1 = foreach sv1 generate 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week', (sum(Sales)/sum(unique_week)) as 'sum_SalesVelocity';sv1 = fill sv1 by (dateCols=('Order_ActivatedDate_Year','Order_ActivatedDate_Week', "Y-W"));
sv1 = timeseries sv1 generate 'sum_SalesVelocity' as 'SalesVelocity' with (length=4, predictionInterval=80, 
    dateCols=('Order_ActivatedDate_Year', 'Order_ActivatedDate_Week', "Y-W"), seasonality=12);
sv1 = foreach sv1 generate 'Order_ActivatedDate_Year' + "~~~" + 'Order_ActivatedDate_Week' as 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week', coalesce('sum_SalesVelocity', 'SalesVelocity') as 'SalesVelocity', 'SalesVelocity_low_80', 'SalesVelocity_high_80';
sv1 = order sv1 by 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week' desc;
--OutOfStock
oos = load "${App.Datasets.CG_RetailVisitKpi5_tp.FullyQualifiedName}";
 oos = filter oos by 'ActualBooleanValue'== "True" and 'Type_Computed' == "OutOfStock";
oos_result = group oos by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week');
oos_result = foreach oos_result generate 'ActualVisitStartTime_Year', 'ActualVisitStartTime_Week',
 count(oos) as 'OutOfStock_ActualCount1';
oos_result = fill oos_result by (dateCols=('ActualVisitStartTime_Year','ActualVisitStartTime_Week', "Y-W"));
oos_result = timeseries oos_result generate 'OutOfStock_ActualCount1' as 'OutOfStock_ActualCount' with (length=4, predictionInterval=80, 
    dateCols=('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', "Y-W"), seasonality=12);
oos_result = foreach oos_result generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', coalesce('OutOfStock_ActualCount1', 'OutOfStock_ActualCount') as 'OutOfStock_ActualCount', 'OutOfStock_ActualCount_low_80', 'OutOfStock_ActualCount_high_80';
oos_result = order oos_result by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' desc;
-- Compliance% Calculation
comp = load "${App.Datasets.CG_RetailVisitKpi5_tp.FullyQualifiedName}";
 comp = filter comp by 'Include_In_Compliance' == "True";
comp = group comp by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week','AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name');
comp = foreach comp generate 
 'AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name' as 'Store','ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', 
       avg('Compliance_Percentage') as 'Avg_Compliance_Percentage1';
comp = group comp by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week');
comp = foreach comp generate 
       'ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', 
       avg('Avg_Compliance_Percentage1') as 'Avg_Compliance_Percentage1';
comp = fill comp by (dateCols=('ActualVisitStartTime_Year','ActualVisitStartTime_Week', "Y-W"));
comp = timeseries comp generate 'Avg_Compliance_Percentage1' as 'Avg_Compliance_Percentage' with (length=4, predictionInterval=80, 
    dateCols=('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', "Y-W"), seasonality=12);
comp = foreach comp generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', coalesce('Avg_Compliance_Percentage1', 'Avg_Compliance_Percentage') as 'Avg_Compliance_Percentage', 'Avg_Compliance_Percentage_low_80', 'Avg_Compliance_Percentage_high_80';
comp = order comp by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' desc;

-- FacingsCompliance% Calculation
fcs = load "${App.Datasets.CG_RetailVisitKpi5_tp.FullyQualifiedName}"; fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week','AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name');
fcs = foreach fcs generate 
 'AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name' as 'Store','ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', 
       avg('Compliance_Percentage') as 'Avg_FacingCompliance_Percentage';
fcs = group fcs by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week');fcs = foreach fcs generate        'ActualVisitStartTime_Year', 'ActualVisitStartTime_Week',        avg('Avg_FacingCompliance_Percentage') as 'Avg_FacingCompliance_Percentage';fcs = fill fcs by (dateCols=('ActualVisitStartTime_Year','ActualVisitStartTime_Week', "Y-W"));fcs = timeseries fcs generate 'Avg_FacingCompliance_Percentage' as 'Avg_FacingsCompliance_Percentage' with (length=4, predictionInterval=80,     dateCols=('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', "Y-W"), seasonality=12);fcs = foreach fcs generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', coalesce('Avg_FacingCompliance_Percentage', 'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', 'Avg_FacingsCompliance_Percentage_low_80', 'Avg_FacingsCompliance_Percentage_high_80';fcs = order fcs by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' desc;

-- SOSCompliance% Calculation
sos = load "${App.Datasets.CG_RetailVisitKpi5_tp.FullyQualifiedName}"; sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week','AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name');
sos = foreach sos generate 
 'AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name' as 'Store','ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', 
       avg('Compliance_Percentage') as 'Avg_SOSCompliance_Percentage';
sos = group sos by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week');sos = foreach sos generate        'ActualVisitStartTime_Year', 'ActualVisitStartTime_Week',        avg('Avg_SOSCompliance_Percentage') as 'Avg_SOSCompliance_Percentage';sos = fill sos by (dateCols=('ActualVisitStartTime_Year','ActualVisitStartTime_Week', "Y-W"));sos = timeseries sos generate 'Avg_SOSCompliance_Percentage' as 'Avg_SoSCompliance_Percentage' with (length=4, predictionInterval=80,     dateCols=('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week', "Y-W"), seasonality=12);sos = foreach sos generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', coalesce('Avg_SOSCompliance_Percentage', 'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', 'Avg_SoSCompliance_Percentage_low_80', 'Avg_SoSCompliance_Percentage_high_80';sos = order sos by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' desc;

--Cases
case1 = load "${App.Datasets.CG_Case5_tp.FullyQualifiedName}";
case1 = group case1 by ('CreatedDate_Year', 'CreatedDate_Week');
case1 = foreach case1 generate 'CreatedDate_Year', 'CreatedDate_Week', count() as 'total_cases';
case1 = fill case1 by (dateCols=('CreatedDate_Year','CreatedDate_Week', "Y-W"));
case1 = timeseries case1 generate 'total_cases' as 'Cases_count' with (length=4, predictionInterval=80, 
    dateCols=('CreatedDate_Year', 'CreatedDate_Week', "Y-W"), seasonality=12);
case1 = foreach case1 generate 'CreatedDate_Year' + "~~~" + 'CreatedDate_Week' as 'CreatedDate_Year~~~CreatedDate_Week', coalesce('total_cases', 'Cases_count') as 'Cases_count', 'Cases_count_low_80', 'Cases_count_high_80';
case1 = order case1 by 'CreatedDate_Year~~~CreatedDate_Week' desc;
--final multidataset table
final_table = cogroup 
                visit1 by  'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week' full,
              sale1 by  'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week' full,
              qty1 by 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week' full,
              sv1 by 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week' full,
              oos_result by  'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' full,
              comp by  'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' full, fcs by  'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' full, sos by  'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week' full,case1 by 'CreatedDate_Year~~~CreatedDate_Week';
final_table = foreach final_table generate coalesce(visit1.'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week',sale1.'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week',qty1.'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week',sv1.'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week',oos_result.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week',comp.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week',fcs.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week',sos.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week',case1.'CreatedDate_Year~~~CreatedDate_Week') as'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week',
         sum(sale1.'Sales') as 'Sales',
         sum(sale1.'Sales_low_80') as 'Sales_low_80', 
         sum(sale1.'Sales_high_80') as 'Sales_high_80',
         sum(qty1.'Quantity') as 'Quantity',
         sum(qty1.'Quantity_low_80') as 'Quantity_low_80', 
         sum(qty1.'Quantity_high_80') as 'Quantity_high_80',
         sum(sv1.'SalesVelocity') as 'SalesVelocity',
         sum(sv1.'SalesVelocity_low_80') as 'SalesVelocity_low_80', 
         sum(sv1.'SalesVelocity_high_80') as 'SalesVelocity_high_80',
         sum(oos_result.'OutOfStock_ActualCount') as 'OutOfStock Count',
          sum(oos_result.'OutOfStock_ActualCount_low_80') as 'OutOfStock_ActualCount_low_80', 
         sum(oos_result.'OutOfStock_ActualCount_high_80') as 'OutOfStock_ActualCount_high_80',
         avg(comp.'Avg_Compliance_Percentage') as 'Avg_Compliance_Percentage',
            avg(comp.'Avg_Compliance_Percentage_low_80') as 'Avg_Compliance_Percentage_low_80', 
         avg(comp.'Avg_Compliance_Percentage_high_80') as 'Avg_Compliance_Percentage_high_80',
  avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage',
            avg(fcs.'Avg_FacingsCompliance_Percentage_low_80') as 'Avg_FacingsCompliance_Percentage_low_80', 
         avg(fcs.'Avg_FacingsCompliance_Percentage_high_80') as 'Avg_FacingsCompliance_Percentage_high_80',
  avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage',
            avg(sos.'Avg_SoSCompliance_Percentage_low_80') as 'Avg_SoSCompliance_Percentage_low_80', 
         avg(sos.'Avg_SoSCompliance_Percentage_high_80') as 'Avg_SoSCompliance_Percentage_high_80',
        sum(visit1.'Incomplete_Visits')as 'Incomplete_Visits',
          sum(visit1.'Incomplete_Visits_low_80') as 'Incomplete_Visits_low_80', 
         sum(visit1.'Incomplete_Visits_high_80') as 'Incomplete_Visits_high_80'
,  sum(case1.'Cases_count')as 'Cases_count',
          sum(case1.'Cases_count_low_80') as 'Cases_count_low_80', 
         sum(case1.'Cases_count_high_80') as 'Cases_count_high_80'         

         ;

final_table = final_table = foreach final_table generate
'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week' as 'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week',
coalesce({{cell(static_1.selection,0,"Field_value_trend").asObject()}}, 0) as '{{cell(static_1.selection,0,"Alias_name_trend").asObject()}}', {{cell(static_1.selection,0,"Alias_name_trend").asObject()}}_low_80 as 'low_80' , {{cell(static_1.selection,0,"Alias_name_trend").asObject()}}_high_80 as 'high_80';
final_table = order final_table by 'PlannedVisitStartTime_Year~~~PlannedVisitStartTime_Week' asc;

