floor(date_to_epoch(now())/86400)))) + (string_to_number(substr("{{coalesce(cell(Planned_Start_Time_1.selection,0,"min"),"All").asObject()}}", index_of("{{coalesce(cell(Planned_Start_Time_1.selection,0,"min"),"All").asObject()}}", "day")+4 )) * 86400)) when !("{{coalesce(cell(Planned_Start_Time_1.selection,0,"min"),"All").asObject()}}" matches "All") then toDate(string_to_number("{{coalesce(cell(Planned_Start_Time_1.selection,0,"min"),"All").asObject()}}")/1000) else toDate(1) end as 'Min', case when "{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}" matches "year" then  year_last_day(toDate( ( number_to_string(string_to_number(toString (now(), "yyyy")) + string_to_number(substr("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", index_of("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", "year")+5 )),"#")+"-01-01"), "yyyy-MM-dd")) when "{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}" matches "quarter" then quarter_last_day(toDate(date_to_epoch(quarter_last_day(toDate(date_to_epoch(quarter_last_day(now())) - 1036800 + ((string_to_number(substr("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", index_of("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", "quarter")+8 ))-1) * 7948800)))) + 86400)) when "{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}" matches "month" then month_last_day(toDate(date_to_epoch(month_last_day(toDate(date_to_epoch(month_last_day(now())) - 1036800 + ((string_to_number(substr("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", index_of("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", "month")+6 ))-1) * 2678400)))) + 86400)) when "{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}" matches "week" then week_last_day(toDate(date_to_epoch(week_last_day(toDate(date_to_epoch(week_last_day(now())) + ((string_to_number(substr("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", index_of("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", "week")+5 ))-1) * 604800)))) + 86400)) when "{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}" matches "day" then toDate((date_to_epoch(now()) - (date_to_epoch(now())- (86400 * floor(date_to_epoch(now())/86400)))) + (string_to_number(substr("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", index_of("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}", "day")+4 )) * 86400)) when !("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}" matches "All") then toDate(string_to_number("{{coalesce(cell(Planned_Start_Time_1.selection,0,"max"),"All").asObject()}}")/1000) else toDate(5707696000) end as 'Max';
