Clone_of_Master_bar_1.query:
-- Incomplete High Priority Visits

visit1 = load "${App.Datasets.CG_Visit5_tp.FullyQualifiedName}";
visit1 = filter visit1 by 'Status' in ["Abandoned", "Planned"] and  'VisitPriority' == "High" and  date('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Month', 'PlannedVisitStartTime_Day') in [.."1 day ago"];
;
visit1 = group visit1 by ('RetailStore_Name','RetailStore_RetailStoreGroup_Name');
visit1 = foreach visit1 generate 'RetailStore_Name' as 'Store','RetailStore_RetailStoreGroup_Name' as 'Store_Group',count(visit1) as 'High_Priority_Incomplete_Visits';

-- Order Details
order1 = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
order1 = group order1 by( 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name', 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name','Order_ActivatedDate_Year','Order_ActivatedDate_Week');
order1 = foreach order1 generate 'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name' as 'Store','Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name' as 'Store_Group','Order_ActivatedDate_Year'+"-"+'Order_ActivatedDate_Week' as 'YearWeek', sum('TotalPrice') as 'Sales',unique('OrderId') as 'Orders',sum('Quantity') as 'Quantity';
order1 = group order1 by ({{column(static_3.selection,["Field_value"]).asObject()}});
order1 = foreach order1 generate {{column(static_3.selection,["Field_value"]).asObject()}} as 'Store', {{column(static_3.selection,["Field_value"]).asObject()}} as 'Store_Group', sum('Sales') as 'Sales', sum('Orders') as 'Orders', sum('Quantity') as 'Quantity', (sum('Sales')/unique('YearWeek')) as 'sales_velocity';

-- Share of Shelf

 sos = load "${App.Datasets.CG_RetailVisitKpi5_tp.FullyQualifiedName}";
sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name','AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name');
sos = foreach sos generate sos.'AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name' as 'Store',sos. 'AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name' as 'Store_Group',avg(sos.'Compliance_Percentage') as 'Avg_SoSCompliance_Percentage';
--OutOfStock
oos = load "${App.Datasets.CG_RetailVisitKpi5_tp.FullyQualifiedName}";
