comp = foreach comp generate comp.'AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name' as 'Store',comp.'AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name' as 'Store_Group',avg(comp.'Compliance_Percentage') as 'Avg_Compliance_Percentage';
