nps_result = foreach nps_result generate  nps.'SurveyResponse_SurveySubject_StoreName' as 'Store', nps.'SurveyResponse_SurveySubject_StoreGroup' as 'Store Group',nps.'SurveyResponse_SurveySubject_Account' as 'Account',nps.'SurveyResponse_SurveySubject_State' as 'State',nps.'SurveyResponse_SurveySubject_City' as 'City',nps.'SurveyResponse_SurveySubject_Location' as 'Location',count(nps_A) as 'detractor_count', count(nps_B) as 'promoter_count', count(nps) as 'total_survey';
nps_result = foreach nps_result generate   'Store', 'Store Group', 'Account', 'State', 'City', 'Location','detractor_count'/'total_survey' as 'detractors_percent','promoter_count'/'total_survey' as 'promoters_percent';
nps_result = foreach nps_result generate   'Store', 'Store Group', 'Account', 'State', 'City', 'Location', ('promoters_percent'-'detractors_percent') as 'NPS_score_percentage';
             
--Total Cases
