visit = foreach visit generate  'Store','Store Group','Account','State','City','Location', daysBetween(toDate(last_visited_time), now() ) as 'days_since_last_visited';

--final multidataset table
final_table = cogroup store1 by ('Store','Store Group','Account','State','City','Location') full,order1 by ('Store','Store Group','Account','State','City','Location') full,sos by ('Store','Store Group','Account','State','City','Location') full,fcs by ('Store','Store Group','Account','State','City','Location') full,oos_result by ('Store','Store Group','Account','State','City','Location') full,comp by ('Store','Store Group','Account','State','City','Location') full,promo by ('Store','Store Group','Account','State','City','Location')full,nps_result by ('Store','Store Group','Account','State','City','Location') full,case1 by ('Store','Store Group','Account','State','City','Location') full,visit by ('Store','Store Group','Account','State','City','Location');

final_table = foreach final_table generate coalesce(store1.'Store', order1.'Store', sos.'Store', fcs.'Store', oos_result.'Store',comp.'Store',promo.'Store',nps_result.'Store',case1.'Store',visit.'Store') as 'RetailStore_Name', coalesce(store1.'Location', order1.'Location', sos.'Location', fcs.'Location', oos_result.'Location',comp.'Location',promo.'Location',nps_result.'Location',case1.'Location',visit.'Location') as 'RetailStore_Location_Name',coalesce(store1.'Account', order1.'Account', sos.'Account', fcs.'Account', oos_result.'Account',comp.'Account',promo.'Account',nps_result.'Account',case1.'Account',visit.'Account') as 'RetailStore_Account_Name',coalesce(store1.'State', order1.'State', sos.'State', fcs.'State',oos_result.'State',comp.'State',promo.'State',nps_result.'State',case1.'State',visit.'State') as 'RetailStore_Location_Address_State',coalesce(store1.'City', order1.'City', sos.'City', fcs.'City',oos_result.'City',comp.'City',promo.'City',nps_result.'City',case1.'City',visit.'City') as 'RetailStore_Location_Address_City'
,sum(order1.'Sales') as 'Sales',sum(order1.'Quantity') as 'Quantity',avg(sos.'SoS_Compliance_Percentage') as 'Share_of_Shelf',sum(oos_result.'OOS_Compliance_Percentage') as 'Out_of_Stock',avg(fcs.'FCS_Compliance_Percentage') as 'Facings',avg(promo.'Promo_Compliance_Percentage') as 'Promotions',
sum(nps_result.'NPS_score_percentage') as 'NPS',sum(case1.'Total_Cases') as 'Total Cases',sum(visit.'days_since_last_visited') as 'days_since_last_visit',avg(comp.'Compliance_Percentage') as 'Compliance_Percentage_1';
final_table = filter final_table by 'RetailStore_Name' is not null; 
final_table = order final_table  by 'Compliance_Percentage_1' desc;

TotalPrice_Order_Act_1.query:
