sales = load "${App.Datasets.CG_OrderItem5_tp.FullyQualifiedName}";
sales_A = filter sales by 'Order_ActivatedDate_sec_epoch' >= {{cell(date_parser_1.result, 0, "Min").asString()}} && 'Order_ActivatedDate_sec_epoch' <= {{cell(date_parser_1.result, 0, "Max").asString()}};
sales_B = filter sales by 'Order_ActivatedDate_sec_epoch' >= {{cell(date_parser_1.result, 0, "Compare_Min").asString()}} && 'Order_ActivatedDate_sec_epoch' <= {{cell(date_parser_1.result, 0, "Compare_Max").asString()}};
total_sale = group sales_A by ('Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name','Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name') full,
sales_B by ('Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name','Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name');
total_sale = foreach total_sale generate coalesce(sales_A.'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name', sales_B.'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_RetailStoreGroup_Name') as 'Store_Group',coalesce(sales_A.'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name', sales_B.'Order_AssessmentTaskOrder_AssessmentTask_ActionPlanItem_ActionPlan_Visit_RetailStore_Name') as 'Store', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales';
