        coalesce(total_sale.'Store', total_qty.'Store', total_sv.'Store', total_comp.'Store', total_visit.'Store',total_oos.'Store',total_sos.'Store',total_case.'Store',total_fcs.'Store') as 'Store', coalesce(total_sale.'Store_Group', total_qty.'Store_Group', total_sv.'Store_Group', total_comp.'Store_Group', total_visit.'Store_Group',total_oos.'Store_Group',total_sos.'Store_Group',total_case.'Store_Group',total_fcs.'Store_Group') as 'Store_Group',
         sum(total_sale.'Sales_Decline') as 'Sales_Decline',
         sum(total_sale.'Decline_percent') as 'Sales_Decline_percent',
         sum(total_qty.'Decline') as 'Quantity_Decline',
       sum(total_qty.'Decline_percent') as 'Quantity_Decline_percent',
  sum(total_fcs.'Decline') as 'FCS_Decline', 
  sum(total_fcs.'Decline_percent') as 'FCS_Decline_percent',      sum(total_sv.'SalesVelocity_Decline') as 'SalesVelocity_Decline',
         sum(total_sv.'Decline_percent') as 'SalesVelocity_Decline_percent',
         avg(total_comp.'Decline') as 'CompliancePercent_Decline',
         avg(total_comp.'Decline_percent') as 'CompliancePercent_Decline_percent',
  avg(total_sos.'Decline') as 'SoS_Decline',
         avg(total_sos.'Decline_percent') as 'SoS_Decline_percent',
 sum(total_oos.'Decline') as 'OOS_Decline',
         sum(total_oos.'Decline_percent') as 'OOS_Decline_percent',
         sum(total_visit.'Decline') as 'HighPriorityIncompleteVisits_Decline',
         sum(total_visit.'Decline_percent') as 'HighPriorityIncompleteVisits_Decline_percent',
 sum(total_case.'Decline') as 'Cases_Decline',
         sum(total_case.'Decline_percent') as 'Cases_Decline_percent';
final_table1 = group final_table by  {{column(static_3.selection,["Field_value"]).asObject()}};
final_table1 = foreach final_table1 generate
{{row(static_3.selection,[0],["Field_value","Api_name_for_decline"]).asProjection()}},{{row(static_1.selection,[0],["Field_value_decline","Alias_decline"]).asProjection()}},{{row(static_1.selection,[0],["Field_value_decline_percent","Alias_decline_percent"]).asProjection()}};
final_table1 = filter final_table1 by {{cell(static_1.selection,0,"Alias_decline").asObject()}} is not null;
final_table1 = foreach final_table1 generate
'{{column(static_3.selection,["Api_name_for_decline"]).asObject()}}',{{cell(static_1.selection,0,"Alias_decline").asObject()}},{{cell(static_1.selection,0,"Alias_decline_percent").asObject()}};
final_table1 = order final_table1 by {{cell(static_1.selection,0,"Alias_decline").asObject()}} {{cell(static_2.selection,0,"Text_fordecline").asObject()}};

incomplete_visits_1.query:
-- Incomplete High Priority Visits
visit1 = load "${App.Datasets.CG_Visit5_tp.FullyQualifiedName}";
visit1 = filter visit1 by 'Status' in ["Abandoned", "Planned"] and  'VisitPriority' == "High" and  date('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Month', 'PlannedVisitStartTime_Day') in [.."1 day ago"];

visit1 = group visit1 by all;
visit1 = foreach visit1 generate count(visit1) as 'High_Priority_Incomplete_Visits';

lens_2.query:
q = load "${App.Datasets.CG_Visit5_tp.FullyQualifiedName}";
q = filter q by 'ActualVisitStartTime_Year' is not null;
result = group q by all;
result = foreach result generate count(q) as 'A';
result = limit result 2000;
