query.pigql:
q = load "${App.Datasets.Sales_Data2_tp.FullyQualifiedName}";q = filter q by {{column(SAQL_REGION_QUOTA.selection,["Account_STATE"]).asEquality("Account_STATE")}};q = filter q by {{column(SAQL_REGION_QUOTA.selection,["Account_STATE"]).asEquality("Account_STATE")}};q = filter q by date('SalesDate_Year', 'SalesDate_Month', 'SalesDate_Day') in ["6 quarters ago".."1 quarter ago"];q = filter q by {{column(SAQL_REGION_QUOTA.selection,["Account_STATE"]).asEquality("Account_STATE")}};q = filter q by {{column(SAQL_SALES_REP_QUOTA.selection,["Account_SALES_REP"]).asEquality("Account_SALES_REP")}};q = filter q by {{column(FILTER_CUSTOMER.selection,["Account_CUSTOMER"]).asEquality("Account_CUSTOMER")}};q = filter q by {{column(SAQL_CUSTOMER_QUOTA.selection,["Account_CUSTOMER_SITE"]).asEquality("Account_CUSTOMER_SITE")}};q = group q by ('Account_SALES_REP', 'SalesDate_Year', 'SalesDate_Quarter');
q = foreach q generate 'Account_SALES_REP' as 'Account_SALES_REP', 'SalesDate_Year' + "~~~" + 'SalesDate_Quarter' as 'SalesDate_Year~~~SalesDate_Quarter', sum('SALES_UNITS') as 'sum_Units';
q = group q by all;
q = foreach q generate round(avg('sum_Units'),-1) as avg_Units

q = load "${App.Datasets.Budget_Data_tp.FullyQualifiedName}";q = filter q by {{column(SAQL_REGION_QUOTA.selection,["Account_STATE"]).asEquality("Account_STATE")}};q = filter q by {{column(SAQL_REGION_QUOTA.selection,["Account_STATE"]).asEquality("Account_STATE")}};q = filter q by {{column(FILTER_CUSTOMER.selection,["Account_CUSTOMER"]).asEquality("Account_CUSTOMER")}};q = filter q by {{column(SAQL_SALES_REP_QUOTA.selection,["Account_SALES_REP"]).asEquality("Account_SALES_REP")}};q = filter q by {{column(SAQL_CUSTOMER_QUOTA.selection,["Account_CUSTOMER_SITE"]).asEquality("Account_CUSTOMER_SITE")}};q = filter q by date('TargetDate_Year', 'TargetDate_Month', 'TargetDate_Day') in ["1 quarter ago".."1 quarter ago"];
q = group q by all;
q = foreach q generate 
sum('Target') as 'sum_Target', 
sum('Target')*1 as 'sum_HighTarget', 
sum('Target')*0.75 as 'sum_MidTarget',
sum('Hours') as 'sum_Hours',
sum('Hours')*0.75 as 'sum_MidHours',
sum('Hours')*0.1 as 'sum_HighHours', 
sum('Activities') as 'sum_Activities',
sum('Activities')*0.75 as 'sum_MidActivities',
sum('Activities')*1 as 'sum_HighActivities';
q = limit q 2000;
