query.pigql:
q = load "${App.Datasets.Budget_Data_tp.FullyQualifiedName}";q = filter q by {{column(FILTER_STATE.selection,["Account_STATE"]).asEquality("Account_STATE")}};q = filter q by {{column(FILTER_CUSTOMER.selection,["Account_CUSTOMER"]).asEquality("Account_CUSTOMER")}};q = filter q by {{column(SAQL_SALES_REP_QUOTA.selection,["Account_SALES_REP"]).asEquality("Account_SALES_REP")}};q = filter q by {{column(SAQL_CUSTOMER_QUOTA.selection,["Account_CUSTOMER_SITE"]).asEquality("Account_CUSTOMER_SITE")}};q = filter q by date('TargetDate_Year', 'TargetDate_Month', 'TargetDate_Day') in ["3 quarter ago".."3 quarter ago"];
q = group q by all;
q = foreach q generate 
sum('Target') as 'sum_Target', 
sum('Target')*0.8 as 'sum_HighTarget', 
sum('Target')*0.5 as 'sum_MidTarget',
sum('Hours') as 'sum_Hours',
sum('Hours')*0.5 as 'sum_MidHours',
sum('Hours')*0.8 as 'sum_HighHours', 
sum('Activities') as 'sum_Activities',
sum('Activities')*0.5 as 'sum_MidActivities',
sum('Activities')*0.8 as 'sum_HighActivities';
q = limit q 2000;
