CSAT___Customer_Journ_1.query:
config = load "${App.Datasets.Journey_Survey_Question_Config_Metadata.FullyQualifiedName}";
config = filter config by 'Context' == "Customer CSAT"; 
config = filter config by 'QuestionType' == "Rating";

q = load "${App.Datasets.SurveyQuestionResponsewithDetails.FullyQualifiedName}"; 
q = filter q by 'Question_QuestionType' == "Rating";
q = group q by ('Id', 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId', 'QuestionChoices_Name', 'JourneyConfig_Stage'); 
q = foreach q generate 'Id' , 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId', 'QuestionChoices_Name' as 'QuestionChoices_Name', 'JourneyConfig_Stage', first('CreatedDate_sec_epoch') as 'CreatedDate_sec_epoch',  first('NumberValue') as 'NumberValue'; 
q = order q by ('SurveyVersion_Survey_Id' asc, 'SurveyVersionId' asc, 'QuestionId' asc, 'QuestionChoices_Name' asc); 
q = group q by ('Id', 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId', 'JourneyConfig_Stage'); 
q = foreach q generate 'Id' , 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId', 'JourneyConfig_Stage', string_to_number(last('QuestionChoices_Name')) as 'Max Rating', round(first('NumberValue')/string_to_number(last('QuestionChoices_Name')),4) as 'Rating%',  first('NumberValue') as 'NumberValue'; 


journey = group config by ('Stage') left, q by ('JourneyConfig_Stage');

journey = foreach journey generate config.'Stage' as 'Stage', first(config.'StageOrder') as 'StageOrder', (sum(q.'Rating%') * first(config.'Scale'))/count(q) * 20 as 'CSAT';
journey = order journey by 'StageOrder' asc;

Responsive_customer__1.query:
q = load "${App.Datasets.SurveyQuestionResponsewithDetails.FullyQualifiedName}";q = filter q by {{column(CSAT___Customer_Journ_1.selection, ["Stage"]).asEquality("JourneyConfig_Stage")}} && {{column(Responses_by_stage_1.selection, ["Stage"]).asEquality("JourneyConfig_Stage")}} && {{column(CSAT___Customer_Journ_4.selection, ["Stage"]).asEquality("JourneyConfig_Stage")}};
config = load "${App.Datasets.Journey_Survey_Question_Config_Metadata.FullyQualifiedName}";
config = filter config by 'Context' == "Customer CSAT";

responsive = group config by all left, q by all;
responsive = foreach responsive generate "Responsive" as 'Customer Type', coalesce(unique(q.'Invitation_ParticipantId'),0) as 'Count';
contacts = load "${App.Datasets.Contacts.FullyQualifiedName}";
passive = group config by all left, q by all left, contacts by all;
passive = foreach passive generate "Untapped" as 'Customer Type', count(contacts) - coalesce(unique(q.'Invitation_ParticipantId'),0) as 'Count';

result = union responsive, passive;
result = group result by 'Customer Type';
result = foreach result generate 'Customer Type', sum('Count') as 'Count';

