QuestionAnalysis_1.query:
q = load "${App.Datasets.SurveyQuestionResponsewithDetails_tp.FullyQualifiedName}";
rating = filter q by 'Question_QuestionType' in ["Rating"];
rating = group rating by ('Id', 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId', 'QuestionChoices_Name');
rating = foreach rating generate 'Id' , 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId', 'QuestionChoices_Name' as 'QuestionChoices_Name', first('CreatedDate_sec_epoch') as 'CreatedDate_sec_epoch',  first('NumberValue') as 'NumberValue', first('Question_Name') as 'Question_Name', first('Question_QuestionType') as 'Question_QuestionType';
rating = order rating by ('SurveyVersion_Survey_Id' asc, 'SurveyVersionId' asc, 'QuestionId' asc, 'QuestionChoices_Name' asc);
rating = group rating by ('Id', 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId');
rating = foreach rating generate 'Id' , 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId', first('NumberValue')/string_to_number(last('QuestionChoices_Name')) * 5 as 'Rating' , first('Question_Name') as 'Question_Name', first('Question_QuestionType') as 'Question_QuestionType';
rating = group rating by ('SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId');
rating = foreach rating generate first('Question_Name') as 'Question_Name', avg('Rating') as 'Value';
rating = order rating by 'Value' asc;

Response_and_abandon_1.query:
invitations = load "${App.Datasets.SurveyInvitation_tp.FullyQualifiedName}";
responses = load "${App.Datasets.SurveyResponseWithDetails_tp.FullyQualifiedName}";
completed = filter responses by 'Status' == "Completed";
abandoned = filter responses by 'Status' == "Started";
result = group invitations by ('SurveyId', 'Survey_Name') left, completed by ('SurveyId', 'Survey_Name') left, abandoned by ('SurveyId', 'Survey_Name');
result = foreach result generate  invitations.'Survey_Name' as 'Survey_Name', count(abandoned) as 'Abandoned Count',
count(completed) as 'Completed Count',
count(invitations) as 'Invitations Count';
result = group result by all;
result = foreach result generate sum('Invitations Count') as 'sum_# Invitations', sum('Completed Count') as 'sum_# Responses', sum('Abandoned Count')/ sum('Invitations Count') as 'Abandonment Rate', sum('Completed Count')/ sum('Invitations Count') as 'Response Rate';

NPS___Customer_Journ_2.query:
