result = foreach result generate scoring.'Invitation_ParticipantId' as 'Invitation_ParticipantId', 
first(scoring.'QuestionId') as 'Score QuestionId', first(scoring.'Question_Name') as 'Score Question',  
first(choices.'QuestionId') as 'Choice QuestionId', first(choices.'Question_Name') as 'Choice Question';
