Choices_per_NPS_Buck_1.query:
q = load "${App.Datasets.SurveyQuestionResponsewithDetails_tp.FullyQualifiedName}"; choices = filter q by 'QuestionId' in ["{{cell(Cross_Tab_Question_S_1.result, 0, "Choice QuestionId").asObject()}}"];
choices = filter choices by 'ChoiceValue' in all;
allResponsesChoices = group choices by ('Invitation_Participant_Name', 'QuestionId', 'ResponseId', 'ChoiceValue');
allResponsesChoices = foreach allResponsesChoices generate 'Invitation_Participant_Name' as 'Invitation_Participant_Name', 'QuestionId' as 'QuestionId', 'ResponseId' as 'ResponseId', 'ChoiceValue' as 'ChoiceValue', first('CreatedDate_sec_epoch') as 'DateTime', max(first('CreatedDate_sec_epoch')) over([..] partition by ('Invitation_Participant_Name', 'QuestionId')) as 'LatestResponseDateTime';
allResponsesChoices = order allResponsesChoices by ('Invitation_Participant_Name' asc, 'LatestResponseDateTime' desc, 'QuestionId' asc);
latestAllResponseChoices = filter allResponsesChoices by 'DateTime'=='LatestResponseDateTime'; nps = filter q by 'QuestionId' in ["{{cell(Cross_Tab_Question_S_1.result, 0, "Score QuestionId").asObject()}}"];
nps = group nps by ('Invitation_Participant_Name', 'QuestionId', 'ResponseId');
nps = foreach nps generate 'Invitation_Participant_Name' as 'Invitation_Participant_Name', 'QuestionId' as 'QuestionId', 'ResponseId' as 'ResponseId', first('NumberValue') as 'NumberValue', first('CreatedDate_sec_epoch') as 'DateTime', max(first('CreatedDate_sec_epoch')) over([..] partition by ('Invitation_Participant_Name', 'QuestionId')) as 'LatestResponseDateTime', " ^-^ " as 'ChoiceValue';
nps = order nps by ('Invitation_Participant_Name' asc, 'LatestResponseDateTime' desc, 'QuestionId' asc);
nps = filter nps by 'DateTime'=='LatestResponseDateTime';
npsCategoryWithChoices = group nps by ('Invitation_Participant_Name', 'ChoiceValue') full, latestAllResponseChoices by ('Invitation_Participant_Name', 'ChoiceValue');
npsCategoryWithChoices = foreach npsCategoryWithChoices generate coalesce(nps.'Invitation_Participant_Name',latestAllResponseChoices.'Invitation_Participant_Name') as 'Invitation_Participant_Name' , coalesce(nps.'ChoiceValue',latestAllResponseChoices.'ChoiceValue') as 'ChoiceValue', first(nps.'NumberValue') as 'NumberValue';
npsCategoryWithChoices = group npsCategoryWithChoices by ('Invitation_Participant_Name', 'ChoiceValue');
npsCategoryWithChoices = foreach npsCategoryWithChoices generate 'Invitation_Participant_Name' , 'ChoiceValue', case when max(first('NumberValue')) over([..] partition by ('Invitation_Participant_Name')) > 8 then "Promoter" when max(first('NumberValue')) over([..] partition by ('Invitation_Participant_Name')) > 6 then "Passive" else "Detractor" end as 'NPS_bucket';
npsCategoryWithChoices = filter npsCategoryWithChoices by 'ChoiceValue'!= " ^-^ ";
npsCategoryWithChoices = group npsCategoryWithChoices by ('NPS_bucket', 'ChoiceValue');
npsCategoryWithChoices = foreach npsCategoryWithChoices generate 'NPS_bucket', 'ChoiceValue', count() as '# Responses';

