CustomerInteractions_2.query:
q = load "${App.Datasets.B2C_Comms_CustomerInteractionTopic_Demo_tp.FullyQualifiedName}";
q = filter q by date('InteractionDate_Year', 'InteractionDate_Month', 'InteractionDate_Day') in ["14 days ago".."current day"];

q = foreach q generate 'CustomerInteractionId', coalesce('SubscriptionId', "") as 'SubscriptionId', 'InteractionTopic', 'ProcessName', 'Outcome', 'InteractionDate_Year','InteractionDate_Month','InteractionDate_Day',coalesce('CaseNumber', "") as 'CaseNumber', coalesce('CaseReason', "") as 'CaseReason', coalesce('CaseStatus', "") as 'CaseStatus', coalesce('OrderNumber', "") as 'OrderNumber', coalesce('OrderStatus', "") as 'OrderStatus', 'DurationInMinute'*60 as 'DurationInMinute', number_to_string('DurationInMinute'*60, "#,##0") as 'DurationInSecondsText';
q = group q by ('CustomerInteractionId', 'SubscriptionId','InteractionTopic', 'ProcessName', 'InteractionDate_Year', 'InteractionDate_Month', 'InteractionDate_Day', 'Outcome', 'CaseNumber', 'CaseReason', 'CaseStatus', 'OrderNumber', 'OrderStatus', 'DurationInSecondsText');

q = foreach q generate 'CustomerInteractionId' as 'CustomerInteractionId', 'SubscriptionId' as 'SubscriptionId', q.'InteractionDate_Year' + "-" + q.'InteractionDate_Month' + "-" + q.'InteractionDate_Day' as 'InteractionDate', 'CaseNumber' as 'CaseNumber', 'CaseReason' as 'CaseReason', 'CaseStatus' as 'CaseStatus', 'InteractionTopic' as 'InteractionTopic', 'ProcessName' as 'ProcessName',  'Outcome' as 'Outcome', 'OrderNumber' as 'OrderNumber', 'OrderStatus' as 'OrderStatus', first('DurationInMinute') as 'DurationInSeconds', 'DurationInSecondsText';
q = order q by ('InteractionDate' desc, 'SubscriptionId' asc nulls last);
q = limit q 2000;

AHT_1.query:
q = load "${App.Datasets.B2C_Comms_CustomerInteractionTopic_Demo_tp.FullyQualifiedName}";
q = filter q by date('InteractionDate_Year', 'InteractionDate_Month', 'InteractionDate_Day') in ["14 days ago".."current day"];
q = group q by 'CustomerInteractionId';
q = foreach q generate 'CustomerInteractionId' as 'CustomerInteractionId', first('DurationInMinute') as 'Duration';
q= group q by all;
q = foreach q generate avg('Duration')*60 as 'AHT', case when avg('Duration')*60 <= 250 then "rgb(75, 202, 129)" when avg('Duration')*60 > 250 then "rgb(224, 26, 59)" else "rgb(216, 222, 232)" end as 'AHT indicator color', count() as 'count';

AvgDataUsage_1.query:
q = load "${App.Datasets.B2C_Comms_TopUp_and_Usage_Demo_tp.FullyQualifiedName}";
q_B = filter q by date('ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day') in ["30 days ago".."current day"];
q_C = filter q by date('ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day') in ["120 days ago".."31 days ago"];
result = group q by 'SubscriptionID' full, q_B by 'SubscriptionID' full, q_C by 'SubscriptionID';
result = foreach result generate coalesce(q.'SubscriptionID', q_B.'SubscriptionID', q_C.'SubscriptionID') as 'SubscriptionID', first(q.'Subscription_DaysSinceSubscription') as 'DaysSinceSubscription', coalesce(sum(q_B.'DataUsageInGB'),0) as 'DataUsageInGB_Last30Days', sum(q_C.'DataUsageInGB')/3 as 'DataUsageInGB_Prev3MonthsWith30DayAvg';
result = foreach result generate 'SubscriptionID', 'DaysSinceSubscription', 'DataUsageInGB_Last30Days', 'DataUsageInGB_Prev3MonthsWith30DayAvg', case when 'DaysSinceSubscription' >= 120 then ('DataUsageInGB_Last30Days' - 'DataUsageInGB_Prev3MonthsWith30DayAvg') / 'DataUsageInGB_Prev3MonthsWith30DayAvg' end as 'DataUsage_Change_Percentage', case when 'DaysSinceSubscription' >= 120 then "Vs. Last 90 Days" else "" end as 'DataUsage_Change_Percentage_Label', case when 'DaysSinceSubscription' >= 120 then "Total data usage during the last 30 days versus the daily average of 30-day intervals for the last 90 days." else "" end as 'DataUsage_Change_Percentage_Label_Tooltip', case when 'DaysSinceSubscription' >= 120 then "rgb(255,255,255)" else "rgba(255,255,255,0)" end as 'Color',
case when 'DaysSinceSubscription' < 120 then  "rgb(75, 202, 129)" when 'DataUsageInGB_Last30Days' > 'DataUsageInGB_Prev3MonthsWith30DayAvg' then "rgb(75, 202, 129)" when 'DataUsageInGB_Last30Days' == 'DataUsageInGB_Prev3MonthsWith30DayAvg' then "rgb(103, 122, 151)" when 'DataUsageInGB_Last30Days' < 'DataUsageInGB_Prev3MonthsWith30DayAvg' then "rgb(224, 26, 59)"  else  "rgb(75, 202, 129)" end as 'Data Usage indicator color';
result = order result by ('SubscriptionID' asc);
result = limit result 2000;

TopUpFrequency_1.query:
q = load "${App.Datasets.B2C_Comms_TopUp_and_Usage_Demo_tp.FullyQualifiedName}";
q = filter q by 'TopupAmount' > 0;

currentperiod = filter q by date('ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day') in ["90 days ago".."current day"];
currentperiod = group currentperiod by ('SubscriptionID', 'ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day');
currentperiod = foreach currentperiod generate 'SubscriptionID' as 'SubscriptionID',
currentperiod.'ActivityDate_Year' as 'ActivityDate_Year' , currentperiod.'ActivityDate_Month' as 'ActivityDate_Month', currentperiod.'ActivityDate_Day' as 'ActivityDate_Day',first(ActivityDate_sec_epoch) as 'A';
currentperiod = group currentperiod by ('SubscriptionID', 'ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day');
currentperiod = foreach currentperiod generate 'SubscriptionID' as 'SubscriptionID','ActivityDate_Year' + "~~~" + 'ActivityDate_Month' + "~~~" + 'ActivityDate_Day' as 'ActivityDate_Year~~~ActivityDate_Month~~~ActivityDate_Day', first(A) as 'A', sum(A) - sum(sum(A)) over ([-1..-1] partition by all order by ('ActivityDate_Year','ActivityDate_Month','ActivityDate_Day')) as 'B';
currentperiod = filter currentperiod by B is not null;

prevperiod = filter q by date('ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day') in ["180 days ago".."91 days ago"];
prevperiod = group prevperiod by ('SubscriptionID', 'ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day');
prevperiod = foreach prevperiod generate 'SubscriptionID' as 'SubscriptionID',
prevperiod.'ActivityDate_Year' as 'ActivityDate_Year' , prevperiod.'ActivityDate_Month' as 'ActivityDate_Month', prevperiod.'ActivityDate_Day' as 'ActivityDate_Day',first(ActivityDate_sec_epoch) as 'A';
prevperiod = group prevperiod by ('SubscriptionID', 'ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day');
prevperiod = foreach prevperiod generate 'SubscriptionID' as 'SubscriptionID', 'ActivityDate_Year' + "~~~" + 'ActivityDate_Month' + "~~~" + 'ActivityDate_Day' as 'ActivityDate_Year~~~ActivityDate_Month~~~ActivityDate_Day', first(A) as 'A', sum(A) - sum(sum(A)) over ([-1..-1] partition by all order by ('ActivityDate_Year','ActivityDate_Month','ActivityDate_Day')) as 'B';
prevperiod = filter prevperiod by B is not null;

result = group q by 'SubscriptionID' full, currentperiod by 'SubscriptionID' full, prevperiod by 'SubscriptionID';
result = foreach result generate q.'SubscriptionID' as 'SubscriptionID', first(q.'Subscription_Name') as 'Subscription Name', case when first(q.'Subscription_DaysSinceSubscription') < 180 then daysBetween(toDate(last(q.'ActivityDate_sec_epoch')), now()) else avg(currentperiod.B)/60/60/24 end  as 'Frequency Days currentperiod'
,case when first(q.'Subscription_DaysSinceSubscription') < 180 then 0 else avg(prevperiod.B)/60/60/24 end as 'Frequency Days prevperiod', 
case when first(q.'Subscription_DaysSinceSubscription') >= 180 then (avg(currentperiod.B) - avg(prevperiod.B))/avg(prevperiod.B) *100 end as 'Frequency Days percent change', 
case when first(q.'Subscription_DaysSinceSubscription') < 180 then "rgb(75, 202, 129)" when (avg(currentperiod.B) - avg(prevperiod.B))/avg(prevperiod.B) *100 > 0 then "rgb(75, 202, 129)" when (avg(currentperiod.B) - avg(prevperiod.B))/avg(prevperiod.B) *100 == 0 then "rgb(103, 122, 151)" when (avg(currentperiod.B) - avg(prevperiod.B))/avg(prevperiod.B) *100 <0 then "rgb(224, 26, 59)"  else "rgb(75, 202, 129)" end as 'Frequency Days indicator color'
,case when first(q.'Subscription_DaysSinceSubscription') < 180 then "Days Since Last Payment" else "Payment Frequency (Days)" end as 'Label',case when first(q.'Subscription_DaysSinceSubscription') < 180 then "" else "Vs. Last 90 Days" end as 'LastTopup_Change_Percentage_Label'
,case when first(q.'Subscription_DaysSinceSubscription') < 180 then "rgba(255,255,255,0)" else "rgb(255,255,255)" end as 'Color';

