Revenue_Liability_Ti_1.query:
q = load "${App.Datasets.LoyaltyMemberCurrencyTrending33.FullyQualifiedName}";
pp = filter q by date('ValidFrom_Year', 'ValidFrom_Month', 'ValidFrom_Day') in [dateRange([1970, 1, 1],  {{coalesce(cell(Min_of_Selected_Date_1.result, 0, "Selected_Date_Min"),"[2050,01,01]").asString()}})]; --Filter for the previous period get the min date across all the members for the selected duration.
pp = group pp by ('LoyaltyMemberId','ValidFrom_Year', 'ValidFrom_Month','ValidFrom_Day','ValidFrom');
pp = foreach pp generate 'LoyaltyMemberId','ValidFrom','ValidFrom_Year', 'ValidFrom_Month', max('ValidFrom_sec_epoch') as 'ValidFrom_sec_epoch',
rank() over ([..] partition by ('LoyaltyMemberId') order by max('ValidFrom_sec_epoch') desc ) as 'ValidFromDatesec_Epoc_Rank',
sum('PointsBalance_Amount') as 'PointsBalance_Amount', 
max('ValidFrom_sec_epoch') as 'max_ValidFrom_number';
pp = filter pp by 'ValidFromDatesec_Epoc_Rank'==1; -- Filters the oldest record and its points for each ProgramMemberId

cp = filter q by ({{row(ActivityDate_2.selection, [0], ["min", "max"]).asDateRange("date('ValidFrom_Year', 'ValidFrom_Month', 'ValidFrom_Day')")}}); --date range for selected period
cp = group cp by ('LoyaltyMemberId','ValidFrom_Year', 'ValidFrom_Month','ValidFrom');
cp = foreach cp generate 'LoyaltyMemberId','ValidFrom','ValidFrom_Year', 'ValidFrom_Month', max('ValidFrom_sec_epoch') as 'ValidFrom_sec_epoch',
rank() over ([..] partition by ('LoyaltyMemberId','ValidFrom_Year', 'ValidFrom_Month') order by max('ValidFrom_sec_epoch') desc ) as 'ValidFromDatesec_Epoc_Rank',
sum('PointsBalance_Amount') as 'PointsBalance_Amount', 
max('ValidFrom_sec_epoch') as 'max_ValidFrom_number';
cp = filter cp by 'ValidFromDatesec_Epoc_Rank'==1;

un = union pp,cp; --Combine Previous period numbers with the current period values
un = group un by('LoyaltyMemberId','ValidFrom_Year', 'ValidFrom_Month','ValidFrom');
un = foreach un generate 
'LoyaltyMemberId','ValidFrom','ValidFrom_Year', 'ValidFrom_Month', sum('PointsBalance_Amount') as 'PointsBalance_Amount',

rank() over ([..] partition by ('LoyaltyMemberId','ValidFrom_Year', 'ValidFrom_Month') order by max('ValidFrom_sec_epoch') desc ) as 'ValidFromDatesec_Epoc_Rank';
un = filter un by 'ValidFromDatesec_Epoc_Rank'==1; --Filter out the previous value for the same months

--Fill up the missing months
result = group un by ('ValidFrom_Year', 'ValidFrom_Month','LoyaltyMemberId','ValidFrom');
result = foreach result generate 'ValidFrom_Year', 'ValidFrom_Month','ValidFrom',  'LoyaltyMemberId', 
sum('PointsBalance_Amount') as 'PointsBalance_Amount';
result = fill result by (dateCols=('ValidFrom_Year', 'ValidFrom_Month', "Y-M"), partition = 'LoyaltyMemberId');
result = group result by ('ValidFrom_Year', 'ValidFrom_Month', 'LoyaltyMemberId','ValidFrom');
result = foreach result generate 'LoyaltyMemberId', 'ValidFrom_Year','ValidFrom', 'ValidFrom_Month',  sum('PointsBalance_Amount') as 'PointsBalance_Amount',
sum(sum('PointsBalance_Amount')) over ([..0] partition by 'LoyaltyMemberId' 
order by (result.'ValidFrom_Year' + "~~~" + result.'ValidFrom_Month')) as 'PartitionKey'; --Create a Partition Key which can be used to partition by points for each member
result = group result by ('ValidFrom_Year', 'ValidFrom_Month', 'PartitionKey', 'LoyaltyMemberId','ValidFrom');
result = foreach result generate 'LoyaltyMemberId', 'ValidFrom_Year', 'ValidFrom','ValidFrom_Month', "1" as 'ValidFrom_Day', 'PartitionKey',
sum(sum('PointsBalance_Amount')) over ([..0] partition by ('PartitionKey', 'LoyaltyMemberId') 
order by (result.'ValidFrom_Year' + "~~~" + result.'ValidFrom_Month')) as 'PointsBalance_Amount';

---Add date filter before timeseries test
result = filter result by ({{row(ActivityDate_2.selection, [0], ["min", "max"]).asDateRange("date('ValidFrom_Year', 'ValidFrom_Month', 'ValidFrom_Day')")}}); --Current Date selection date range
result = group result by ('ValidFrom_Year', 'ValidFrom_Month');
result = foreach result generate 'ValidFrom_Year'+"~~~"+'ValidFrom_Month' as 'ValidFrom_Year~~~ValidFrom_Month','ValidFrom_Year', 'ValidFrom_Month',  sum('PointsBalance_Amount') as 'PointsBalance_Amount';

