Voucher = load "${App.Datasets.Voucher46.FullyQualifiedName}";
Voucher = filter Voucher by ({{row(ActivityDate_2.selection, [0], ["min", "max"]).asDateRange("date('EffectiveDate_Year', 'EffectiveDate_Month', 'EffectiveDate_Day')")}});
Voucher = filter Voucher by 'Status' == "Issued";
Voucher = group Voucher by ('EffectiveDate_Year', 'EffectiveDate_Month');
--Filter for current date selection

Voucher = foreach Voucher generate 'EffectiveDate_Year', 'EffectiveDate_Month',
sum('VoucherDefinition.CostPerVoucher') as 'VoucherLiablity_Amt';

--Revenue
re = load "${App.Datasets.TransactionJournal66.FullyQualifiedName}";
re = filter re by ({{row(ActivityDate_2.selection, [0], ["min", "max"]).asDateRange("date('ActivityDate_Year', 'ActivityDate_Month', 'ActivityDate_Day')")}});
re = filter re by 'Status' == "Processed"; re = filter re by 'JournalType_Name' in ["Accrual"];
--Filter for currecnt date selection

re = foreach re generate 'MemberId','ActivityDate_Year','ActivityDate_Month',sum('TransactionAmount') as 'Revenue';

 --Combine all the streams Trending points_amount, Voucher, Revenue
final = cogroup result by ('ValidFrom_Year', 'ValidFrom_Month') full, Voucher by ('EffectiveDate_Year', 'EffectiveDate_Month') full, re by ('ActivityDate_Year', 'ActivityDate_Month');
final = foreach final generate coalesce(result.'ValidFrom_Year', Voucher.'EffectiveDate_Year', re.'ActivityDate_Year') as 'ValidFrom_Year',
coalesce(result.'ValidFrom_Month', Voucher.'EffectiveDate_Month', re.'ActivityDate_Month') as 'ValidFrom_Month',
sum(result.'PointsBalance_Amount') as 'PointsBalance_Amount',
sum(Voucher.'VoucherLiablity_Amt') as 'VoucherLiablity_Amt',
sum(re.'Revenue') as 'Revenue';

