final = foreach final generate 'ValidFrom_Year','ValidFrom_Month','ValidFrom_Year'+"~~~"+'ValidFrom_Month' as 'ValidFrom_Year~~~ValidFrom_Month', sum('PointsBalance_Amount') as 'PointsBalance_Amount', sum('VoucherLiablity_Amt') as 'VoucherLiablity_Amt',
