final = foreach final generate 'ValidFrom_Year', 'ValidFrom_Month', 'Revenue', case when 'PastFutureFlag' == "Past" || 'PastFutureFlag' == "Future" && 'PointsBalance_Amount' > 0 then 'Liability' end as 'Liability', 'PointsBalance_Amount', 'VoucherLiablity_Amt';
final = timeseries final generate 'Liability' as 'Predicted_Liability' with (length=3, dateCols=('ValidFrom_Year','ValidFrom_Month',"Y-M"));
final = foreach final generate 'ValidFrom_Year'+"~~~"+'ValidFrom_Month' as 
'ValidFrom_Year~~~ValidFrom_Month', 
sum('Revenue') as 'Revenue', 
--sum('Liability') as 'Liability', sum('Predicted_Liability') as 'Predicted_Liability', 
coalesce(sum('Liability'), sum('Predicted_Liability')) as 'Liability';

