Selected_Duration_1.query:
q = load "${App.Datasets.LoyaltyMemberTier66.FullyQualifiedName}";
q = group q by ('LoyaltyProgramMember.EnrollmentDate');
q = foreach q generate 
'LoyaltyProgramMember.EnrollmentDate' as 'LoyaltyProgramMember.EnrollmentDate';
q = order q by 'LoyaltyProgramMember.EnrollmentDate' desc;
q = limit q 1;
q = foreach q generate "["+replace(('LoyaltyProgramMember.EnrollmentDate'),"-",",")+"]" as 'Current_Year_LastDay';

Members_By_Tier_1.query:
q = load "${App.Datasets.LoyaltyMemberTier66.FullyQualifiedName}";
q = filter q by 'LoyaltyProgramMember.MemberType' != "Group";
q = filter q by date('LoyaltyProgramMember.EnrollmentDate_Year', 'LoyaltyProgramMember.EnrollmentDate_Month', 'LoyaltyProgramMember.EnrollmentDate_Day') in [dateRange([1970,1,30], {{coalesce(cell(Selected_Duration_1.result, 0, "Current_Year_LastDay"), "[2021,12,31]").asString()}})];
q = group q by 'LoyaltyTier.Name';
q = foreach q generate 'LoyaltyTier.Name', unique('LoyaltyMemberId') as 'unique_LoyaltyMemberId';
q = order q by 'unique_LoyaltyMemberId' desc;
q = limit q 2000;

LoyaltyTierGroup_Nam_1.query:
q = load "${App.Datasets.LoyaltyProgramTier66.FullyQualifiedName}";
q = filter q by 'LoyaltyProgram.Name' == "{{cell(LoyaltyProgram_Name_1.selection, 0, "LoyaltyProgram.Name").asString()}}";
q = group q by ('LoyaltyTierGroup.Name','LoyaltyTierGroup.IsPrimary');
q = order q by 'LoyaltyTierGroup.IsPrimary' desc;
q = foreach q generate 'LoyaltyTierGroup.Name' as 'LoyaltyTierGroup.Name', count() as 'count';
q = limit q 2000;

