q = limit q 500;

NQPCurrency_Name_1_1.query:
q = load "${App.Datasets.LoyaltyProgramCurrency62.FullyQualifiedName}";
q = filter q by 'CurrencyType' == "NonQualifying";
q = filter q by 'LoyaltyProgram.Name' == "{{cell(LoyaltyProgram_Name_1.selection, 0, "LoyaltyProgram.Name").asString()}}";
q = group q by ('NQP_Currency', 'CurrencyType','IsPrimary');
q = order q by ('IsPrimary' desc, 'CurrencyType' asc);
