q_Current = filter q by {{row(ActivityDate_1.selection, [0], ["min", "max"]).asDateRange("date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day')")}};
q_CompareTo = filter q by {{row(ActivityDate_2.selection, [0], ["min", "max"]).asDateRange("date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day')")}};
result = group q_Current by all full, q_CompareTo by all;
result = foreach result generate count(q_Current) as 'Current_Tier_Changes', 
(count(q_Current) - count(q_CompareTo)) / count(q_CompareTo) as 'TierChanges_CompareToDuration_percentage';
result = limit result 1;

lens_1.query:
q = load "${App.Datasets.LoyRcg_SurveyQuestionResponse_with_TransactionJournalRecords53.FullyQualifiedName}";
q = filter q by 'SurveySubject_Participant_User_UniqueUserName' == "{{coalesce(cell(Agent_Performance_1.selection, 0, "User_UniqueUserName"), cell(Agent_Performance_1.result, 0, "User_UniqueUserName")).asString()}}";
q = filter q by 'SurveyQuestion_QuestionType' == "Rating";
result = foreach q generate 'SurveySubject_Participant_User_UniqueUserName' as 'Agent', first('NumberValue')/first('QuestionChoice_MaxValue') as 'Value', count(q) as 'Count', first('Scale') as 'Scale';
result = group result by 'Agent';
result = foreach result generate 'Agent',round((sum(Value)/sum(Count)) * first('Scale'),2)  as 'Program CSAT';

lens_2.query:
q = load "${App.Datasets.LoyRcg_Team_Performance_Table.FullyQualifiedName}";
q = group q by ('Agent');
q = foreach q generate 'Agent' as 'Agent', first('SmallPhotoUrl') as 'SmallPhotoUrl', sum('Modifications') as 'sum_Modifications', sum('Members_Assisted') as 'sum_Members_Assisted';
q = order q by ('Agent' asc);
q = limit q 2000;
