ForecastsTimeline_1.query:
q = load "${App.Datasets.Demo_Revenue_Insights.FullyQualifiedName}";
q = filter q by {{coalesce(cell(QuotaAttainmentFull_1.selection, 0, "UserName"), cell(forecast_managers.selection, 0, "Manager")).asEquality('UserName')}};
q = filter q by date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day') in [.."current day"];
result = group q by ('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day');
result = foreach result generate q.'CloseDate_Year' + "~~~" + q.'CloseDate_Month' + "~~~" + q.'CloseDate_Day' as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', sum(q.'ClosedWonForecast') as 'A';
result = group result by 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day';
result = foreach result generate 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', first(A) as 'A', sum(sum(A)) over ([..0] partition by all order by ('CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day')) as 'Cumulative_Amount';
result = order result by ('CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day' asc);
start_forecast = group result by all;
start_forecast = foreach start_forecast generate last('CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day') as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', last('Cumulative_Amount') as 'Cumulative_Amount';

a1 = load "${App.Datasets.Demo_Revenue_Insights.FullyQualifiedName}";
a1 = filter a1 by {{coalesce(cell(saql_directs_quota_attainment.selection, 0, "User.Name"), cell(forecast_managers.selection, 0, "Manager")).asEquality('UserName')}};
a1 = group a1 by all;
end_forecast = foreach a1 generate "{{cell(time_period.result, 0, "last_day_in_time_period").asString()}}" as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', {{coalesce(cell(InsightsOverviewKPIs_1.result, 0, "B"),0).asString()}} as 'Cumulative_Amount';
forecast_line = union start_forecast, end_forecast;
final = group result by 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day' full, forecast_line by 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day';
final = foreach final generate coalesce(result['CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day'], forecast_line['CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day']) as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', first(result['Cumulative_Amount']) as 'Closed Won Sales', first(forecast_line['Cumulative_Amount']) as 'Commit Forecast';

TeamStats_1.query:
q = load "${App.Datasets.Demo_Revenue_Insights.FullyQualifiedName}";
q_B = filter q by date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day') in [.."current day"];
result = group q by 'UserName' full, q_B by 'UserName';
result = foreach result generate coalesce(q.'UserName', q_B.'UserName') as 'UserName', sum(q.'Quota') as 'Quota', sum(q_B.'ClosedWon') as 'ClosedWonUntilToday', sum(q.'CommitForecast') as 'CommitForecastEnd';
result = foreach result generate 'UserName', 'Quota', 'ClosedWonUntilToday', 'CommitForecastEnd', case when ClosedWonUntilToday >= Quota then 1 else 0 end as 'MeetingQuota', case when CommitForecastEnd >= Quota then 1 else 0 end as 'ForecastedToMeetQuota';
result = group result by all;
result = foreach result generate sum('MeetingQuota') as 'TotalMeetingQuota', sum('ForecastedToMeetQuota') as 'TotalForecastedToMeetQuota', count() as 'Total', (sum('MeetingQuota'))/count() as 'PrcMeetingQuota', (sum('ForecastedToMeetQuota'))/count() as 'PrcForecastedToMeetQuota', (count() - sum('ForecastedToMeetQuota'))/count() as 'PrcForecastedToNotMeetQuota';

