niqueUserName' full, Percentage_Left by 'User.UniqueUserName'; c = foreach c generate coalesce(first(u['UniqueUserName']), first(q['User.UniqueUserName']), first(AvgDeal_Left['User.UniqueUserName']), first(OpptyCount_Left['User.UniqueUserName']), first(r['User.UniqueUserName']), first(quota['Quota.UniqueUserName']), first(Avg_Day['User.UniqueUserName']), first(Percentage['User.UniqueUserName']), first(Percentage_Left['User.UniqueUserName'])) as 'UniqueUserName',  coalesce(sum(q['Amount']),0) as 'sum_Amount', (coalesce(sum(AvgDeal_Left['Amount']),0) / coalesce(count(OpptyCount_Left),0))  as 'sum_left_DealAmount', coalesce(count(OpptyCount_Left),0) as 'sum_left_count', coalesce(sum(leftpipe['Amount']),0) as 'sum_pipe', coalesce((sum(Avg_Day['CloseDate_day_epoch']) - sum(Avg_Day['CreatedDate_day_epoch']))/count(Avg_Day),0) as 'sum_DaysOpen',  coalesce(count(q) / count(r),0) as 'sum_win_rate', coalesce(sum(Percentage_Left['CompletedRate_Left']),0) as 'sum_LeftActivity', coalesce(sum(q['Amount']) / sum(quota['QuotaAmount']),0) as 'sum_QuotaAmount'; 
c = group c by all;
c = foreach c generate {{cell(team_selector_4.selection,0, "refline").asObject()}};

time_period.query:
q = load "${Apps.RevenueInsights.Datasets.opportunity_revinsights.FullyQualifiedName}";
q = group q by all;
q = foreach q generate date_to_string(now(), "yyyy") + "~~~" + date_to_string(now(), "MM") + "~~~" + date_to_string(now(), "dd") as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day',  number_to_string(daysBetween(now(), fiscal_quarter_last_day(now())),"##0") as 'days_left_in_time_period', date_to_string(fiscal_quarter_last_day(now()),"yyyy~~~MM~~~dd") as 'last_day_in_time_period';

