Team_Performance_Current_User.query:
opp = load "${App.Datasets.opportunity_snapshot32_tp.FullyQualifiedName}"; 
opp = filter opp by {{cell(Current_User.selection, 0, "User_Name").asEquality('User_Name')}}; 
opp = filter opp by 'ForecastCategoryName' in ["Closed"]; 
opp = filter opp by date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day') in [.."current day"]; 
quota = load "${App.Datasets.forecastingquota34_tp.FullyQualifiedName}"; 
quota = filter quota by {{cell(Current_User.selection, 0, "User_Name").asEquality('Quota_UniqueUserName')}}; 
k = group opp by all; 
k = foreach k generate "1970" as 'Date_Year', "01" as 'Date_Month', "01" as 'Date_Day'; 
k = group k by ('Date_Year', 'Date_Month', 'Date_Day'); 
k = foreach k generate 'Date_Year', 'Date_Month', 'Date_Day', count() as 'sum'; 
k = fill k by (dateCols=('Date_Year','Date_Month','Date_Day', "Y-M-D")); 
k = timeseries k generate 'sum' as 'cnt' with (length=25000,  dateCols=('Date_Year','Date_Month','Date_Day', "Y-M-D")); 
k = filter k by {{row(CloseDate_1.selection, [0], ["min", "max"]).asDateRange("date('Date_Year','Date_Month', 'Date_Day')")}}; 
k = filter k by date('Date_Year','Date_Month', 'Date_Day') in [.."current day"]; 
k = group k by all; 
k1 = foreach k generate first('Date_Year') as 'CloseDate_Year', first('Date_Month') as 'CloseDate_Month', first('Date_Day') as 'CloseDate_Day', " - " as 'User_UniqueUserName'; 
k2 = foreach k generate last('Date_Year') as 'CloseDate_Year', last('Date_Month') as 'CloseDate_Month', last('Date_Day') as 'CloseDate_Day', " - " as 'User_UniqueUserName'; 
k3 = union k1,k2; 
a = group opp by ('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day', 'User_UniqueUserName'); 
a = foreach a generate 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day', 'User_UniqueUserName', sum(a.'Amount') as 'A'; 
c = union a,k3; 
c = foreach c generate 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day','User_UniqueUserName', 'A'; 
c = fill c by (dateCols=('CloseDate_Year','CloseDate_Month','CloseDate_Day', "Y-M-D"), partition='User_UniqueUserName'); 
c = filter c by 'User_UniqueUserName' not in [" - "]; 
c = group c by ('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day', 'User_UniqueUserName'); 
c = foreach c generate c.'CloseDate_Year' + "~~~" + c.'CloseDate_Month' + "~~~" + c.'CloseDate_Day' as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', 'User_UniqueUserName', first(A) as 'A', 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day'; 
c = group c by ('CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', 'User_UniqueUserName'); 
c = foreach c generate 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', 'User_UniqueUserName', first(A) as 'A', first('CloseDate_Year') as 'CloseDate_Year', first('CloseDate_Month') as 'CloseDate_Month', first('CloseDate_Day') as 'CloseDate_Day', sum(sum(A)) over ([..0] partition by 'User_UniqueUserName' order by ('CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day')) as 'sum_Amount'; 
c = timeseries c generate 'sum_Amount' as 'estimate' with (partition='User_UniqueUserName',length=100, predictionInterval=95, ignoreLast=false, dateCols=('CloseDate_Year','CloseDate_Month','CloseDate_Day', "Y-M-D")); 
c = foreach c generate 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day', 'CloseDate_Year' + "~~~" + 'CloseDate_Month' + "~~~" + 'CloseDate_Day' as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', 'User_UniqueUserName', A, sum_Amount, estimate, estimate_high_95, estimate_low_95; 
c = filter c by {{row(CloseDate_1.selection, [0], ["min", "max"]).asDateRange("date('CloseDate_Year','CloseDate_Month', 'CloseDate_Day')")}}; 
c = group c by 'User_UniqueUserName'; 
c = foreach c generate 'User_UniqueUserName', last('estimate') as 'sum_prediction'; 
q = cogroup opp by 'User_UniqueUserName' left, quota by 'Quota_UniqueUserName' left, c by 'User_UniqueUserName'; 
q = foreach q generate coalesce(opp.'User_UniqueUserName', quota.'Quota_UniqueUserName', c.'User_UniqueUserName') as 'User_UniqueUserName', first(opp.'User_SmallPhotoUrl') as 'Photo', sum(opp.'Amount') as 'sum_Amount', sum(quota.'QuotaAmount') as 'sum_QuotaAmount', sum(opp.'Amount')/sum(quota.'QuotaAmount')*100 as 'sum_quota_attainment', sum(c.'sum_prediction')/sum(quota.'QuotaAmount')*100 as 'sum_user_prediction',sum(c.'sum_prediction') as sum_prediction_amount ; 
q = order q by 'sum_user_prediction' desc nulls last;

Pipe_Coverage.query:
q1 = load "${App.Datasets.opportunity_snapshot32_tp.FullyQualifiedName}";
q1 = filter q1 by {{coalesce(cell(Forecasting_Team_Performance_Directs.selection, 0, "User_UniqueUserName"), cell(Current_User.selection, 0, "User_Name")).asEquality('User_Name')}};
q2 = load "${App.Datasets.forecastingquota34_tp.FullyQualifiedName}";
q1 = filter q1 by 'IsClosed' == "false";
q = cogroup q1 by all left, q2 by all;
q = foreach q generate sum(q1.'Amount') as 'A', sum(q2.'QuotaAmount') as 'B';
q = foreach q generate 'A', 'B', A/B as 'Pipe_Coverage';
q = limit q 2000;

