quota_attainment.query:
q = load "${App.Datasets.opportunity41_tp.FullyQualifiedName}";
q = filter q by 'IsClosed' == "true";
q = filter q by 'IsWon' == "true";
a = load "${App.Datasets.forecastingquota34_tp.FullyQualifiedName}";
result = group q by all full, a by all;
result = foreach result generate sum(q.'Amount')/sum(a.'QuotaAmount')*100 as 'quota_attainment';
result = foreach result generate 'quota_attainment', (case when 'quota_attainment' < 33 then "#f71979" when 'quota_attainment' < 66 then "#fed444" when 'quota_attainment' >= 66 then "#3ae867" else "#7D98B3" end) as 'color';

TimeStamp.query:
q = load "${App.Datasets.opportunity41_tp.FullyQualifiedName}";
q = group q by all;
q = foreach q generate "Today: " + date_to_string(now(), "MMM dd, yyyy") + " | " +  number_to_string(daysBetween(now(), quarter_last_day(now())),"##0") + " days left in quarter" as 'time';

gap_to_goal.query:
q = load "${App.Datasets.opportunity41_tp.FullyQualifiedName}";
q = filter q by 'IsClosed' == "true";
q = filter q by 'IsWon' == "true";
a = load "${App.Datasets.forecastingquota34_tp.FullyQualifiedName}";
result = group q by all full, a by all;
result = foreach result generate sum(q.'Amount') - sum(a.'QuotaAmount') as 'sum_Amount';
result = foreach result generate 'sum_Amount', (case when 'sum_Amount' > 0 then "#4ED469" when 'sum_Amount' < 0 then "#ED3251" else "#7D98B3" end) as 'color'

prediction_1.query:
q = load "${App.Datasets.opportunity41_tp.FullyQualifiedName}";
opp = filter q by 'IsClosed' == "true";
opp = filter opp by 'IsWon' == "true";
opp = filter opp by date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day') in [.."current day"];
k = group opp by all;
k = foreach k generate "1970" as 'Date_Year', "01" as 'Date_Month', "01" as 'Date_Day';
k = group k by ('Date_Year', 'Date_Month', 'Date_Day');
k = foreach k generate 'Date_Year', 'Date_Month', 'Date_Day', count() as 'sum';
k = fill k by (dateCols=('Date_Year','Date_Month','Date_Day', "Y-M-D"));
k = timeseries k generate 'sum' as 'cnt' with (length=25000,  dateCols=('Date_Year','Date_Month','Date_Day', "Y-M-D")); k = filter k by {{row(CloseDate_1.selection, [0], ["min", "max"]).asDateRange("date('Date_Year','Date_Month', 'Date_Day')")}};
k = filter k by date('Date_Year','Date_Month', 'Date_Day') in [.."current day"];
k = group k by all;
k1 = foreach k generate first('Date_Year') as 'CloseDate_Year', first('Date_Month') as 'CloseDate_Month', first('Date_Day') as 'CloseDate_Day';
k2 = foreach k generate last('Date_Year') as 'CloseDate_Year', last('Date_Month') as 'CloseDate_Month', last('Date_Day') as 'CloseDate_Day';
k3 = union k1,k2;
a = group opp by ('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day');
a = foreach a generate 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day', sum(a.'Amount') as 'A';
c = union a,k3;
c = foreach c generate 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day', 'A';
c = fill c by (dateCols=('CloseDate_Year','CloseDate_Month','CloseDate_Day', "Y-M-D"));
c = group c by ('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day');
c = foreach c generate c.'CloseDate_Year' + "~~~" + c.'CloseDate_Month' + "~~~" + c.'CloseDate_Day' as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', first(A) as 'A', 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day';
c = group c by ('CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day');
c = foreach c generate 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', first(A) as 'A', first('CloseDate_Year') as 'CloseDate_Year', first('CloseDate_Month') as 'CloseDate_Month', first('CloseDate_Day') as 'CloseDate_Day', sum(sum(A)) over ([..0] partition by all order by ('CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day')) as 'sum_Amount';
c = timeseries c generate 'sum_Amount' as 'estimate' with (length=100, predictionInterval=95, ignoreLast=false, dateCols=('CloseDate_Year','CloseDate_Month','CloseDate_Day', "Y-M-D"));
c = foreach c generate 'CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day', 'CloseDate_Year' + "~~~" + 'CloseDate_Month' + "~~~" + 'CloseDate_Day' as 'CloseDate_Year~~~CloseDate_Month~~~CloseDate_Day', A, sum_Amount, estimate, estimate_high_95, estimate_low_95; c = filter c by {{row(CloseDate_1.selection, [0], ["min", "max"]).asDateRange("date('CloseDate_Year','CloseDate_Month', 'CloseDate_Day')")}};
c = group c by all;
c = foreach c generate last('estimate') as 'sum_prediction',  last('estimate_high_95') as 'upper_bound', last('estimate_low_95') as 'lower_bound';

chart_open_opptys_activities.query:
q = load "${App.Datasets.opportunity41_tp.FullyQualifiedName}";
q = filter q by 'IsClosed' == "false";
q = group q by 'Name';
q = foreach q generate 'Name' as 'Name', sum('Amount') as 'sum_Amount', sum('Activity_ActivityCount') as 'sum_Activities';
q = order q by 'sum_Amount' desc;

chart_open_opptys_amountchanges.query:
q = load "${App.Datasets.opportunity_snapshot32_tp.FullyQualifiedName}";
q = filter q by 'IsClosed' == "false";
q = order q by 'SnapshotTextDate' desc;
q = group q by ('SnapshotTextDate', 'Name');
latest = foreach q generate 'SnapshotTextDate' as 'SnapshotTextDate', date_to_string(toDate(first('SnapshotDate_sec_epoch') - (2*86400)), "yyyy-MM-dd") as '7 days ago', 'Name' as 'Name', sum('Amount') as 'Amount';
prev = foreach q generate 'SnapshotTextDate', 'Name', date_to_string(toDate(first('SnapshotDate_sec_epoch') - (2*86400)), "yyyy-MM-dd") as '7 days ago', sum('Amount') as 'Amount';
result = group latest by ('7 days ago', 'Name') full, prev by ('SnapshotTextDate', 'Name');
result = foreach result generate latest.'7 days ago' as '7 days ago', latest.'Name' as 'Name', first(latest.'Amount') as 'A', sum(prev.'Amount') as 'B';
result = order result by '7 days ago' desc;
result = foreach result generate first('Name') as 'Name', first('A') as 'sum_Amount',  first('A') - first('B') as 'Amount Changes';
result = order result by 'sum_Amount' desc;
