Actions_Distribution_1.query:
LoyaltyLedger = load "${App.Datasets.LoyaltyLedger_ManualActions7.FullyQualifiedName}";
LoyaltyLedger = filter LoyaltyLedger by 'User_UniqueUserName' == "{{coalesce(cell(Agent_Polar_1.result, 0, "User_UniqueUserName"), "NA").asString()}}";
LoyaltyLedger = filter LoyaltyLedger by {{column(NQPCurrency_Name_1.selection, ["LoyaltyProgramCurrency_NQP_Currency"]).asEquality('LoyaltyProgramCurrency_NQP_Currency')}} or ('LoyaltyProgramCurrency_QP_Currency' is not null && {{column(QPCurrency_Name_1.selection, ["LoyaltyProgramCurrency_QP_Currency"]).asEquality('LoyaltyProgramCurrency_QP_Currency')}});
LoyaltyLedger = group LoyaltyLedger by 'TransactionType';
LoyaltyLedger = foreach LoyaltyLedger generate case when 'TransactionType' == "AccrualReversal" then "1.AccrualReversal" when 'TransactionType' == "RedemptionReversal" then "2.RedemptionReversal" when 'TransactionType' == "AdjustedPoints_Positive" then "3.AdjustedPoints_Positive" when 'TransactionType' == "AdjustedPoints_Negative" then "4.AdjustedPoints_Negative" end as 'TransactionType', unique('TransactionJournalId') as 'Total_Actions';

MemberTierHistory = load "${App.Datasets.LoyaltyMemberTierHistory7.FullyQualifiedName}";
MemberTierHistory = filter MemberTierHistory by 'User_UniqueUserName' == "{{coalesce(cell(Agent_Polar_1.result, 0, "User_UniqueUserName"), "NA").asString()}}";
MemberTierHistory = group MemberTierHistory by all;
MemberTierHistory = foreach MemberTierHistory generate "5.Tier Changes" as 'TransactionType', count() as 'Total_Actions';
Result = cogroup LoyaltyLedger by 'TransactionType' full, MemberTierHistory by 'TransactionType';
Result = foreach Result generate coalesce(LoyaltyLedger.'TransactionType', MemberTierHistory.'TransactionType') as 'ActionType', 
coalesce(sum(LoyaltyLedger.'Total_Actions') , sum(MemberTierHistory.'Total_Actions')) as 'Total_Actions';
Result = order Result by 'ActionType' asc;

