Agent_Performance_Trend_1.query:
LoyaltyLedger = load "${App.Datasets.LoyaltyLedger_ManualActions7.FullyQualifiedName}";
LoyaltyLedger = filter LoyaltyLedger by 'User_UniqueUserName' == "{{coalesce(cell(Agent_Polar_1.result, 0, "User_UniqueUserName"), "NA").asString()}}";
LoyaltyLedger = filter LoyaltyLedger by {{column(NQPCurrency_Name_1.selection, ["LoyaltyProgramCurrency_NQP_Currency"]).asEquality('LoyaltyProgramCurrency_NQP_Currency')}} or ('LoyaltyProgramCurrency_QP_Currency' is not null && {{column(QPCurrency_Name_1.selection, ["LoyaltyProgramCurrency_QP_Currency"]).asEquality('LoyaltyProgramCurrency_QP_Currency')}});
MemberTierHistory = load "${App.Datasets.LoyaltyMemberTierHistory7.FullyQualifiedName}";
MemberTierHistory = filter MemberTierHistory by 'User_UniqueUserName' == "{{coalesce(cell(Agent_Polar_1.result, 0, "User_UniqueUserName"), "NA").asString()}}";
Result =  group LoyaltyLedger by ('ActivityDate_Year', 'ActivityDate_Month','LoyaltyProgramMemberId') full, MemberTierHistory by ('CreatedDate_Year', 'CreatedDate_Month', 'LoyaltyMemberTier_LoyaltyMemberId');
Result = foreach Result generate coalesce(LoyaltyLedger.'ActivityDate_Year', MemberTierHistory.'CreatedDate_Year') as 'ActivityDate_Year', coalesce(LoyaltyLedger.'ActivityDate_Month', MemberTierHistory.'CreatedDate_Month') as 'ActivityDate_Month', coalesce(LoyaltyLedger.'LoyaltyProgramMemberId',MemberTierHistory.'LoyaltyMemberTier_LoyaltyMemberId') as 'LoyaltyMemberId', unique(LoyaltyLedger.'TransactionJournalId') as 'Ledger_ManualActions', count(MemberTierHistory) as 'Tier_Changes';

LoyaltyLedger_AccrualReversal = filter LoyaltyLedger by 'TransactionType' == "AccrualReversal";
LoyaltyLedger_RedemptionReversal = filter LoyaltyLedger by 'TransactionType' == "RedemptionReversal";
LoyaltyLedger_CreditedPoints = filter LoyaltyLedger by 'TransactionType' == "AdjustedPoints_Positive";
LoyaltyLedger_DebitedPoints = filter LoyaltyLedger by 'TransactionType' == "AdjustedPoints_Negative";

Result_Final = group Result by ('ActivityDate_Year', 'ActivityDate_Month') full, LoyaltyLedger_AccrualReversal by ('ActivityDate_Year', 'ActivityDate_Month') full, LoyaltyLedger_RedemptionReversal by ('ActivityDate_Year', 'ActivityDate_Month') full, LoyaltyLedger_CreditedPoints by ('ActivityDate_Year', 'ActivityDate_Month') full, LoyaltyLedger_DebitedPoints by ('ActivityDate_Year', 'ActivityDate_Month');

Result_Final = foreach Result_Final generate Result.'ActivityDate_Year' as 'ActivityDate_Year', Result.'ActivityDate_Month' as 'ActivityDate_Month', sum(Result.'Ledger_ManualActions') as 'Ledger_ManualActions', sum(Result.'Tier_Changes') as 'Tier_Changes', coalesce(sum(Result.'Ledger_ManualActions'),0) + coalesce(sum(Result.'Tier_Changes'),0) as 'Total_Actions',  unique(Result.'LoyaltyMemberId') as 'Members_Serviced', sum(LoyaltyLedger_AccrualReversal.'Points') as 'Accrual_Reversal', sum(LoyaltyLedger_RedemptionReversal.'Points') as 'Redemption_Reversal', sum(LoyaltyLedger_CreditedPoints.'Points') as 'AdjustedPoints_Positive', sum(LoyaltyLedger_DebitedPoints.'Points') as 'AdjustedPoints_Negative', (coalesce(sum(LoyaltyLedger_RedemptionReversal.'Points'),0) + coalesce(sum(LoyaltyLedger_CreditedPoints.'Points'),0) - coalesce(sum(LoyaltyLedger_AccrualReversal.'Points'),0) - coalesce(sum(LoyaltyLedger_DebitedPoints.'Points'),0)) as 'Net_Adjusted_Points';
Final = foreach Result_Final generate 'ActivityDate_Year' + "~~~" + 'ActivityDate_Month' as 'ActivityDate_Year~~~ActivityDate_Month',coalesce('{{cell(KPI_Selection_1.selection, 0, "Metric").asString()}}',0) as '{{cell(KPI_Selection_1.selection, 0, "Metric").asString()}}', coalesce('{{cell(KPI_Selection_2.selection, 0, "Metric").asString()}}',0) as '{{cell(KPI_Selection_2.selection, 0, "Metric_Alias").asString()}}';
Final = filter Final by '{{cell(KPI_Selection_1.selection, 0, "Metric").asString()}}' != 0 || '{{cell(KPI_Selection_2.selection, 0, "Metric_Alias").asString()}}' != 0;

Adjusted_Points_KPIs_1.query:
