q_CurrentDuration_RedemptionReversal = filter q_CurrentDuration by 'TransactionType' == "RedemptionReversal";
q_CurrentDuration_CreditedPoints = filter q_CurrentDuration by 'TransactionType' == "AdjustedPoints_Positive";
q_CurrentDuration_DebitedPoints = filter q_CurrentDuration by 'TransactionType' == "AdjustedPoints_Negative";

q_CompareToDuration_AccrualReversal = filter q_CompareToDuration by 'TransactionType' == "AccrualReversal";
q_CompareToDuration_RedemptionReversal = filter q_CompareToDuration by 'TransactionType' == "RedemptionReversal";
q_CompareToDuration_CreditedPoints = filter q_CompareToDuration by 'TransactionType' == "AdjustedPoints_Positive";
q_CompareToDuration_DebitedPoints = filter q_CompareToDuration by 'TransactionType' == "AdjustedPoints_Negative";

